/**
 * <p>
 * <b> Clase abtracta del validar que define el mensaje de error. </b>
 * </p>
 */
public class AbstractValidator {
    /**
     * <p>
     * <b> Mensaje de error del validador. </b>
     * </p>
     */
    private String messageError;

    /**
     * Gets the message error.
     *
     * @return el messageError
     */
    public final String getMessageError() {
        return this.messageError;
    }

    /**
     * Sets the message error.
     *
     * @param messageError            el messageError a establecer
     */
    public final void setMessageError(final String messageError) {
        this.messageError = messageError;
    }


}
